import axios from "axios";
import { useRef, useState } from "react";
import { Modal, Button } from "react-bootstrap";

function AddEmployee(props) {
  const [selectedFile, setselectedFile] = useState();
  const profileImage = useRef();

  const onFileChange = (event) => {
    event.preventDefault();
    setselectedFile(event.target.files[0]);
  };

  function UploadImage() {
    var myHeaders = new Headers();
    myHeaders.append("Accept", "application/json");

    const formData = new FormData();
    formData.append("Avatar", selectedFile);
    formData.append("Name", "some value user types");

    // const config = {
    //     headers: {
    //         'content-type': 'multipart/form-data',
    //     },
    // };
    // const HTTP = axios.create({
    // });
    // //return post(url, formData, config);
    // return HTTP.post("https://localhost:44374/api/Employees/EmployeeImage", formData, config);

    // axios.post(
    //   "https://localhost:44374/api/Employees/EmployeeImage",
    //   formData,
    //   config
    // );

    axios
      .post("https://localhost:44374/api/Employees/EmployeeImage", formData, {
          headers:{ "content-type": "multipart/form-data"}
        
      })
      .then((response) => {
        console.log(" response :",response);
      })
      .then((data) => {
        console.log("data: ", data);
      });

    // fetch("https://localhost:44374/api/Employees/EmployeeImage", {
    //   method: "POST",
    // //   headers: { "Content-Type": "application/json" },
    //   body: { formData },
    // })
    //   .then((response) => {
    //     console.log("response: ", response);
    //   })
    //   .then((data) => {
    //     console.log("data: ", data);
    //   })
    //   .catch((error) => console.log("erro: ", error));
  }

  return (
    <Modal show={props.show}>
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="mb-3">
          <label htmlFor="formFile" className="form-label">
            Default file input example
          </label>
          <input
            onChange={onFileChange}
            className="form-control"
            type="file"
            id="formFile"
            ref={profileImage}
          />
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.hide}>
          Close
        </Button>
        <Button variant="primary" onClick={UploadImage}>
          Upload Image
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default AddEmployee;
