import { useRef, useState } from "react";
import { Modal, Button } from "react-bootstrap";

function AddEmployee(props) {
  const [selectedFile, setselectedFile] = useState();
  const profileImage = useRef();

  const onFileChange = (event) => {
    event.preventDefault();
    setselectedFile(event.target.files[0]);
    // const formData = new FormData();
    // formData.append("myFile", event.target.files[0], event.target.files[0].name);
    // console.log("file information", typeof(event.target.files[0]));
  };

  function UploadImage() {
    const formData = new FormData();
    formData.append("myFile", selectedFile, selectedFile.name);
     


    fetch(`http://.../gallery/${path}`, {
        method: 'POST',
        headers: {'Content-Type': 'multipart/form-data'},
        body: {formData}
    })
    .then((response) => 
    {
      Console.log()
    })
    .then((data) => {
        this.setState({images: data.images, isLoading: false});
        this.props.updateImages(data.images);
    })
    .catch(error => this.setState({error, isLoading: false}));

  }

  return (
    <Modal show={props.show}>
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="mb-3">
          <label htmlFor="formFile" className="form-label">
            Default file input example
          </label>
          <input
            onChange={onFileChange}
            className="form-control"
            type="file"
            id="formFile"
            ref={profileImage}
          />
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.hide}>
          Close
        </Button>
        <Button variant="primary" onClick={UploadImage}>
          Upload Image
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

export default AddEmployee;
