import React, { useEffect, useState } from "react";
import DepartmentTable from "../componets/DepartMents/DepartmentTable";
import { LoadDepartments } from "../Services/DepartmentSerivces";

function Department() {
  const [loadedDepartments, setLoadedDepartments] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    LoadDepartments().then((data) => {
      setLoading(false);
      setLoadedDepartments(data);
    });
  }, []);

  function AddNewDepartment(){
    console.log("add department");
  };

  var loadingView = "";
  if (loading) {
    loadingView = <span>Loadind....</span>;
  } else {
    loadingView = (
      <React.Fragment>
        <DepartmentTable data={loadedDepartments} />
        <button
          type="button"
          className="btn btn-primary"
          onClick={AddNewDepartment}
        >
          Add Department
        </button>
      </React.Fragment>
    );
  }

  return <div className="mx-5">{loadingView}</div>;
}

export default Department;
