import { useRef } from "react";
import { Modal, Button } from "react-bootstrap";

function AddEmployee(props) {
    const profileImage = useRef();
    

    const UploadImage = (event) => {
        event.preventDefault();
        const departmentIDInput = profileImage.current.value;
        console.log('hellow', event.target.files[0])
    };
    
  return (
    <Modal show={props.show}>
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="mb-3">
          <label htmlFor="formFile" className="form-label">
            Default file input example
          </label>
          <input onChange={UploadImage} className="form-control" type="file" id="formFile" ref={profileImage}/>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.hide}>
          Close
        </Button>
        <Button variant="primary" onClick={UploadImage}>Upload Image</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default AddEmployee;
