import ServiceConstants from "../constants/ServiceConstants"

export function LoadDepartments(){
    return fetch(ServiceConstants.BasedUrl.concat(ServiceConstants.DeapertmentCpontroller))
    .then(data => data.json())
    .catch((error) => {
        
      });
}

export function AddDepartments(dat){
  return fetch(ServiceConstants.BasedUrl.concat(ServiceConstants.DeapertmentCpontroller),{
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(data)
  })
  .then(data => data.json())
  .catch((error) => {
      
    });
}
